package com.icodeap.sentencias_control;

public class EjecutorFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int suma = 5;
		for (int i =1; i <=10; i++) {
			suma = suma +2;
			System.out.println("Esto hace el bucle for: "+ i);
		}
		
		System.out.println("El valor de suma es :"+ suma);

	}

}
